CREATE OR REPLACE FUNCTION obter_desc_prioridade_reg ( nr_sequencia_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE


/*
Descrição - D
Nível prioridade - N
*/
ds_retorno_w           varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_opcao_p = 'D') then
		select 	substr(ds_prioridade,1,255)
		into STRICT	ds_retorno_w
		from 	prioridade_regulacao
		where 	nr_sequencia = nr_sequencia_p;
	elsif (ie_opcao_p = 'N') then
		select 	max(ie_prioridade)
		into STRICT	ds_retorno_w
		from 	prioridade_regulacao
		where 	nr_sequencia = nr_sequencia_p;
	end if;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
