SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_problema (ie_tipo_registro_p text, ie_tipo_problema_p text, nm_tabela_p text default null) RETURNS varchar AS $body$
DECLARE


/* EI - Escalas e indices
   DM - Diagnósticos do médico
   DE - Diagnósticos da enfermagem
   CP - CIAP
   LP - Cadastro da Lista de problema
   HS - Historico de Saude*/
ds_retorno_w		varchar(255);

BEGIN

if (ie_tipo_registro_p = 'EI') then
	ds_retorno_w := SUBSTR(obter_descricao_padrao('VICE_ESCALA','DS_INFORMACAO',ie_tipo_problema_p),1,255);
elsif (ie_tipo_registro_p = 'DM') then
	ds_retorno_w := substr(obter_desc_cid(ie_tipo_problema_p),1,255);
elsif (ie_tipo_registro_p = 'DE') then
	ds_retorno_w := substr(PE_obter_desc_diag(ie_tipo_problema_p,'DI'),1,255);
elsif (ie_tipo_registro_p = 'CP') then
	ds_retorno_w := substr(obter_desc_ciap(ie_tipo_problema_p),1,255);
elsif (ie_tipo_registro_p = 'LP') then
	select	substr(coalesce(max(ds_problema),''),1,255)
	into STRICT	ds_retorno_w
	from	CADASTRO_PROBLEMA
	where	nr_sequencia = ie_tipo_problema_p;
elsif (ie_tipo_registro_p = 'HS') then
	if (ie_tipo_problema_p IS NOT NULL AND ie_tipo_problema_p::text <> '') then
		ds_retorno_w := substr(Obter_desc_hist_saude(ie_tipo_problema_p),1,255);
	elsif (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
		if (nm_tabela_p = 'PACIENTE_ALERGIA') then
			ds_retorno_w := obter_desc_expressao(314948);
		elsif (nm_tabela_p = 'PACIENTE_ANTEC_CLINICO') then
			ds_retorno_w := obter_desc_expressao(314080) || '/ ' || obter_desc_expressao(314081);
		elsif (nm_tabela_p = 'PACIENTE_HABITO_VICIO') then
			ds_retorno_w := obter_desc_expressao(291200);
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
