CREATE OR REPLACE FUNCTION obter_desc_procedadic_concat (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	varchar(254);
ds_retorno_w		varchar(4000);


C01 CURSOR FOR
	SELECT  substr(obter_descricao_procedimento(c.cd_procedimento,c.ie_origem_proced),1,254) ds_procedimento
	from	prescr_medica b,
			prescr_procedimento c,
			cirurgia a
	where   a.nr_prescricao = b.nr_prescricao
	and		b.nr_prescricao = c.nr_prescricao
	and		a.nr_cirurgia = nr_cirurgia_p
	order by 1;


BEGIN

open c01;
loop
fetch c01 into
		ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_retorno_w	:= ds_retorno_w||' - '||ds_procedimento_w;

	end;
end loop;
close c01;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
