CREATE OR REPLACE FUNCTION obter_desc_proced_adic ( nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
qt_procedimento_w	integer;
ds_procedimento_w	PROCEDIMENTO.DS_PROCEDIMENTO%type;

ds_retorno_w		varchar(2000);

C01 CURSOR FOR
	SELECT	cd_procedimento,
		qt_procedimento,
		substr(obter_descricao_procedimento(cd_procedimento,ie_origem_proced),1,240)
	from	sus_laudo_proced_adic
	where	nr_seq_laudo	= nr_seq_laudo_p
	order by nr_sequencia;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	cd_procedimento_w,
	qt_procedimento_w,
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w	:= to_char(cd_procedimento_w) ||' / '||to_char(qt_procedimento_w) ||' / '||ds_procedimento_w;

	end;
END LOOP;
close c01;


return ds_retorno_w;

END;
$body$
