CREATE OR REPLACE FUNCTION obter_desc_proced_autor_agenda (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE



ds_lista_proced_w	varchar(2000);
ds_procedimento_w	varchar(255);

c01 CURSOR FOR
	SELECT 	substr(obter_desc_prescr_proc(b.cd_procedimento,b.ie_origem_proced,b.nr_seq_proc_interno),1,255)
	from   	autorizacao_convenio a,
		procedimento_autorizado b,
		estagio_autorizacao c
	where  	a.nr_sequencia 		= b.nr_sequencia_autor
	and	a.nr_seq_estagio 	= c.nr_sequencia
	and	c.ie_interno 		= 10
	and	a.nr_seq_agenda 	= nr_seq_agenda_p;


BEGIN

open c01;
loop
fetch c01 into
	ds_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_lista_proced_w::text, '') = '') then
		ds_lista_proced_w := substr(ds_lista_proced_w || ds_procedimento_w,1,2000);
	else
		ds_lista_proced_w := substr(ds_lista_proced_w || ',' ||ds_procedimento_w,1,2000);
	end if;
	end;
end loop;
close c01;

return substr(ds_lista_proced_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_proced_autor_agenda (nr_seq_agenda_p bigint) FROM PUBLIC;

