CREATE OR REPLACE FUNCTION obter_desc_proc_convenio (nr_seq_propaci_p bigint, nr_seq_partic_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type;


BEGIN

select	max(coalesce(c.ds_procedimento, b.ds_procedimento))
into STRICT	ds_procedimento_w
FROM procedimento b, procedimento_paciente a
LEFT OUTER JOIN proc_paciente_convenio c ON (a.nr_sequencia = c.nr_seq_procedimento)
