CREATE OR REPLACE FUNCTION obter_desc_proc_inter ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	proc_interno
where	cd_procedimento		=	cd_procedimento_p
and	ie_origem_proced	=	ie_origem_proced_p;

if (nr_sequencia_w <> 0) then
	begin
	select	ds_proc_exame
	into STRICT	ds_retorno_w
	from	proc_interno
	where	nr_sequencia	= 	nr_sequencia_w;
	end;
else
	begin

	select	ds_procedimento
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p;
	end;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
