CREATE OR REPLACE FUNCTION obter_desc_proc_loc_mat ( cd_material_p material.cd_material%TYPE, cd_procedimento_loc_p procedimento.cd_procedimento_loc%TYPE, ie_origem_proced_p procedimento.ie_origem_proced%TYPE) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w	varchar(240);


BEGIN

if (coalesce(cd_material_p,0) > 0) then
	select	substr(coalesce(max(ds_material), wheb_mensagem_pck.get_texto(802236)),1,240)
	into STRICT	ds_retorno_w
	from 	material
	where	cd_material = cd_material_p;
else
	select substr(coalesce(max(ds_procedimento), wheb_mensagem_pck.get_texto(802236)),1,240)
	into STRICT ds_retorno_w
	from procedimento
	where cd_procedimento_loc = cd_procedimento_loc_p
	  and ie_origem_proced = coalesce(ie_origem_proced_p,ie_origem_proced);
end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
