CREATE OR REPLACE FUNCTION obter_desc_proc_material ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


/*Essa function é a mesma coisa da OBTER_DESC_PROC_MAT. Mas a function OBTER_DESC_PROC_MAT retorna a frase "Não encontrado" quando não encontra o Material ou o Procedimento.
Tive que criar essa quase igual para retornar vazio*/
ds_retorno_w	varchar(240);


BEGIN

if (coalesce(cd_material_p,0) > 0) then
	select	substr(coalesce(max(ds_material), ''),1,240)
	into STRICT	ds_retorno_w
	from 	material
	where	cd_material = cd_material_p;
else
	select coalesce(max(ds_procedimento), '')
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = coalesce(ie_origem_proced_p,ie_origem_proced);
end if;

return	ds_retorno_w;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
