SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_proc_mat_int ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_propaci_p bigint) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w		varchar(240);
nr_seq_proc_interno_w	bigint;


BEGIN

if (coalesce(cd_material_p,0) > 0) then		--'Não Encontrado'
	select 	substr(coalesce(max(ds_material), wheb_mensagem_pck.get_texto(308664)),1,240)
	into STRICT	ds_retorno_w
	from 	material
	where 	cd_material = cd_material_p;
else
	begin
	select	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_interno_w
	from 	procedimento_paciente
	where 	nr_sequencia = nr_seq_propaci_p;
	exception
	when	others then
		nr_seq_proc_interno_w:= null;
	end;

	if (coalesce(nr_seq_proc_interno_w::text, '') = '') then ----'Não Encontrado'
		select 	coalesce(max(ds_procedimento),wheb_mensagem_pck.get_texto(308664))
		into STRICT 	ds_retorno_w
		from 	procedimento
		where 	cd_procedimento = cd_procedimento_p
		and 	ie_origem_proced = coalesce(ie_origem_proced_p,ie_origem_proced);
	else
		select	obter_desc_proc_interno(nr_seq_proc_interno_w)
		into STRICT	ds_retorno_w
		;
	end if;
end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
