CREATE OR REPLACE FUNCTION obter_desc_proc_mat ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w	varchar(240);


BEGIN

if (coalesce(cd_material_p,0) > 0) then
	select	substr(coalesce(max(ds_material), wheb_mensagem_pck.get_texto(802236)),1,240)
	into STRICT	ds_retorno_w
	from 	material
	where	cd_material = cd_material_p;
else
	select substr(coalesce(max(ds_procedimento), wheb_mensagem_pck.get_texto(802236)),1,240)
	into STRICT ds_retorno_w
	from procedimento
	where cd_procedimento = cd_procedimento_p
	  and ie_origem_proced = coalesce(ie_origem_proced_p,ie_origem_proced);
end if;

RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
