CREATE OR REPLACE FUNCTION obter_desc_proc_paciente (IE_COD_DESC_P text, NR_SEQ_PROCEDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		varchar(100);
ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type;
cd_procedimento_convenio_w	varchar(20);


BEGIN

if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '')  then
	SELECT	a.DS_PROCEDIMENTO,
		b.CD_PROCEDIMENTO,
		b.cd_procedimento_convenio
	INTO STRICT	ds_procedimento_w,
		cd_procedimento_w,
		cd_procedimento_convenio_w
	FROM	PROCEDIMENTO a,
		PROCEDIMENTO_PACIENTE b
	WHERE	b.NR_SEQUENCIA = NR_SEQ_PROCEDIMENTO_P
	AND	a.CD_PROCEDIMENTO = b.CD_PROCEDIMENTO
	AND	a.IE_ORIGEM_PROCED = b.IE_ORIGEM_PROCED;

	if	ie_cod_desc_p = 'C'	then
		return cd_procedimento_w;
	elsif	ie_cod_desc_p = 'CC'	then
		return cd_procedimento_convenio_w;
	else
		return ds_procedimento_w;
	end if;
else
	return null;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
