CREATE OR REPLACE FUNCTION obter_desc_proc_quimio ( nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type	:= null;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_proc_interno_w	bigint;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
	begin

	select	cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w
	from	agenda_consulta
	where	nr_sequencia	= (	SELECT nr_seq_agenda_cons
					from paciente_atendimento
					where nr_seq_atendimento =	nr_seq_atendimento_p);

	if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '' AND ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') or
	   (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '' AND nr_seq_proc_interno_w > 0)
	then
		ds_procedimento_w 	:= obter_exame_agenda(cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w);
	end if;

	end;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
