CREATE OR REPLACE FUNCTION obter_desc_proc ( cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select 	substr(ds_procedimento,1,255)
	into STRICT	ds_retorno_w
	from 	procedimento
	where 	cd_procedimento = cd_procedimento_p;
end if;

RETURN ds_retorno_w;

END;
