SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_prod_lactario ( nr_seq_prod_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
aux_w			varchar(600);
aux2_w			varchar(600);

c01 CURSOR FOR
	SELECT	a.qt_porcentagem || '% '||  substr(obter_desc_material((SELECT 	max(b.cd_material)
									from 	prescr_material b
									where	b.nr_sequencia = a.nr_seq_material
									and	b.nr_prescricao = a.nr_prescricao)), 1, 255)
	from	nut_prod_lac_item_adic a
	where	a.nr_seq_prod_item = nr_seq_prod_item_p;


BEGIN

select	substr(obter_desc_material(a.cd_material), 1, 255) || ' '
			|| obter_desc_solubilidade(a.nr_seq_solubilidade)
into STRICT	aux_w
from	nut_prod_principal a,
	nut_prod_lac_item b
where	a.nr_sequencia = b.nr_seq_prod_princ
and	b.nr_sequencia = nr_seq_prod_item_p;

ds_retorno_w := substr(aux_w, 1, 255);

aux_w := ' (';

open c01;
	loop
	fetch c01 into
		aux2_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (aux_w = ' (') then
				aux_w := aux_w || aux2_w;
			else
				aux_w := aux_w || ' + ' || aux2_w;
			end if;
		end;
	end loop;
close c01;

if (aux_w <> ' (') then
	aux_w := substr(aux_w || ')',1, 255);
	ds_retorno_w := ds_retorno_w || aux_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
