SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_programa_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
nr_seq_participante_w  bigint;
count_w	bigint := 0;
ds_pessoa_pac_config_w	varchar(1);
ds_visualizar_alerta_w	varchar(1);
ds_medicina_preve_w	varchar(255) := 'MEPREV';


C01 CURSOR FOR
SELECT (SELECT x.nm_programa
        FROM   mprev_programa x 
        WHERE  x.nr_sequencia = a.nr_seq_programa) ds_nome 
FROM   mprev_programa_partic a, 
       mprev_participante b 
WHERE  a.nr_seq_participante = b.nr_sequencia 
       AND b.cd_pessoa_fisica = (SELECT Max(cd_pessoa_fisica) 
                                 FROM   mprev_participante 
                                 WHERE  nr_sequencia = nr_seq_participante_w) 
       AND Trunc(coalesce(a.dt_exclusao, clock_timestamp())) >= Trunc(clock_timestamp()) 

UNION ALL
 
SELECT (SELECT x.nm_campanha 
        FROM   mprev_campanha x 
        WHERE  x.nr_sequencia = a.nr_seq_campanha) ds_nome 
FROM   mprev_campanha_partic a, 
       mprev_participante b 
WHERE  a.nr_seq_participante = b.nr_sequencia 
       AND Trunc(coalesce(a.dt_exclusao, clock_timestamp())) >= Trunc(clock_timestamp()) 
       AND b.cd_pessoa_fisica = (SELECT Max(cd_pessoa_fisica) 
                                 FROM   mprev_participante 
                                 WHERE  nr_sequencia = nr_seq_participante_w) 

UNION ALL
 
SELECT (SELECT x.nm_grupo 
        FROM   mprev_grupo_controle x 
        WHERE  x.nr_sequencia = a.nr_seq_grupo_controle) ds_nome 
FROM   mprev_grupo_cont_pessoa a 
WHERE  a.cd_pessoa_fisica = (SELECT Max(cd_pessoa_fisica) 
                             FROM   mprev_participante 
                             WHERE  nr_sequencia = nr_seq_participante_w) 
       AND Trunc(coalesce(a.dt_exclusao, clock_timestamp())) >= Trunc(clock_timestamp())
order by ds_nome;

BEGIN

ds_retorno_w := '<b> <font size="3"> ' || obter_desc_expressao(596757) || ' </font></b>';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')then

	select	coalesce(max('S'), 'N')
	into STRICT	ds_pessoa_pac_config_w
	from	pessoa_paciente_config
	where	ie_campo_paciente = ds_medicina_preve_w;

	if ( ds_pessoa_pac_config_w = 'S' )then
		select	max(a.nr_sequencia)
		into STRICT 	nr_seq_participante_w
		from	mprev_participante a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	
		if (nr_seq_participante_w IS NOT NULL AND nr_seq_participante_w::text <> '') then
		
			select	coalesce(max('S'), 'N')
			into STRICT	ds_visualizar_alerta_w
			from	pessoa_paciente_config
			where	ie_campo_paciente = ds_medicina_preve_w
			and		((cd_pessoa_fisica = obter_pf_usuario_ativo or coalesce(cd_pessoa_fisica::text, '') = '')
			and (cd_perfil = obter_perfil_ativo or coalesce(cd_perfil::text, '') = '')
			and (cd_estabelecimento = obter_estabelecimento_ativo or coalesce(cd_estabelecimento::text, '') = '')
			and (obter_se_especialidades_medico(cd_especialidade, '(' || obter_especialidade_medico_cod(obter_pf_usuario_ativo) || ')') = 'S' or coalesce(cd_especialidade::text, '') = '')
			and (cd_funcao = obter_funcao_ativa or coalesce(cd_funcao::text, '') = ''));
		
		
			if ( ds_visualizar_alerta_w = 'S')then
				for program_prev in C01 loop
					ds_retorno_w :=  ds_retorno_w || '<br/>' || '<br/>' || program_prev.ds_nome;
					count_w := count_w + 1;
				end loop;
			end if;

		end if;
	end if;

end if;

if (coalesce( count_w, 0) <= 0)then
	ds_retorno_w := null;
end if;

if (coalesce(ds_retorno_w::text, '') = '') and (pkg_i18n.get_user_locale() = 'ja_JP') and (obter_parametro_funcao_padrao(281, 1609, wheb_usuario_pck.get_nm_usuario()) = 'S') and (wheb_usuario_pck.get_cd_funcao() = 281)  then

	ds_retorno_w := obter_desc_expressao(1038081);

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_programa_pac (cd_pessoa_fisica_p text) FROM PUBLIC;

