CREATE OR REPLACE FUNCTION obter_desc_propaci_int (nr_seq_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		varchar(15);
ds_procedimento_w		PROCEDIMENTO.DS_PROCEDIMENTO%type;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w		bigint;


BEGIN
select	a.cd_procedimento,
	a.ie_origem_proced,
	a.nr_seq_proc_interno
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w
from	procedimento_paciente a
where	a.nr_sequencia = nr_seq_procedimento_p;

if (coalesce(nr_seq_proc_interno_w::text, '') = '') then
	select	ds_procedimento
	into STRICT	ds_procedimento_w
	from	procedimento
	where	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;
else
	select 	obter_desc_proc_interno(nr_seq_proc_interno_w)
	into STRICT	ds_procedimento_w
	;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
