CREATE OR REPLACE FUNCTION obter_desc_protocolo_medic_rep ( nr_sequencia_p bigint, cd_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	protocolo_medicacao.nm_medicacao%type;


BEGIN

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	a.nm_medicacao
	into STRICT	ds_retorno_w
	from	protocolo_medicacao a
	where	a.cd_protocolo	= cd_protocolo_p
	and	a.nr_sequencia	= (	SELECT p.nr_sequencia
                        from protocolo_medic_solucao p
                        where p.nr_seq_interno = nr_sequencia_p);

end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
