CREATE OR REPLACE FUNCTION obter_desc_protoc_medic ( nr_seq_interna_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_interna_p IS NOT NULL AND nr_seq_interna_p::text <> '') then


	select	substr(b.NM_PROTOCOLO||' - '||a.nm_medicacao,1,255) ds
	into STRICT	ds_retorno_w
	from	PROTOCOLO_MEDICACAO a,
			protocolo b
	where 	b.cd_protocolo = a.cd_protocolo
	and		a.nr_seq_interna = nr_seq_interna_p;


end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
