CREATE OR REPLACE FUNCTION obter_desc_proto_opme_concat (nr_lote_producao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w		varchar(255);
ds_retorno_w		varchar(4000);
nr_seq_protocolo_w	bigint;


C01 CURSOR FOR
	SELECT  substr(b.nm_protocolo,1,255),
		b.nr_sequencia
	FROM 	lote_producao_comp a,
		protocolo_fornec_opme b
	WHERE 	nr_seq_protocolo = b.nr_sequencia
	AND  	a.nr_lote_producao = nr_lote_producao_p
	group by b.nr_sequencia,
		 b.nm_protocolo
	order by 1;


BEGIN

open c01;
loop
fetch c01 into
		ds_protocolo_w,
		nr_seq_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_retorno_w	:= substr(ds_retorno_w||' , '||ds_protocolo_w,1,4000);

	end;
end loop;
close c01;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
