CREATE OR REPLACE FUNCTION obter_desc_prot_medic (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w  	varchar(500);
ds_protocolo_w 	 varchar(500);
ds_medic_w  	varchar(500);

BEGIN
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
 		select 	coalesce(max(obter_desc_protocolo(cd_protocolo)),' '),
  			coalesce(max(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo)),' ')
 		into STRICT 	ds_protocolo_w,
 		 	ds_medic_w
		 from	 paciente_setor
 		where	 nr_seq_paciente = nr_seq_paciente_p;
 		if (ds_protocolo_w <> ' ') or (ds_medic_w <> ' ') then
  			ds_retorno_w := ds_protocolo_w ||' / '||ds_medic_w;
 		end if;
end if;
return ds_retorno_w;
END;
$body$
