CREATE OR REPLACE FUNCTION obter_desc_prot_onc (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_protocolo_w 		varchar(240);
ds_retorno_w		varchar(2000);


BEGIN
select	max(substr(obter_desc_protocolo(b.cd_protocolo),1,250) || ' / ' || substr(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo),1,250))
into STRICT	ds_retorno_w
from	paciente_atendimento a,
	paciente_setor b
where	a.nr_seq_paciente = b.nr_seq_paciente
and	substr(obter_desc_protocolo(b.cd_protocolo),1,250) || ' / ' || substr(obter_desc_protocolo_medic(nr_seq_medicacao, cd_protocolo),1,250) <> ' / '
and	a.nr_seq_atendimento = nr_seq_atendimento_p;


return	ds_retorno_w;

END	;
$body$
