CREATE OR REPLACE FUNCTION obter_desc_radio_group (cd_exp_valores_p tabela_atributo.cd_exp_valores%type, ie_valor_p text) RETURNS varchar AS $body$
DECLARE

				
				

ds_valores_w		varchar(4000);
ds_codigos_w		varchar(4000);
ds_opcoes_w			varchar(4000);
codigos_w			lista_varchar_pck.tabela_varchar;
opcoes_w			lista_varchar_pck.tabela_varchar;

BEGIN

ds_valores_w := replace(trim(both substr(obter_desc_expressao(cd_exp_valores_p),1,4000)),chr(10),'');

if (ds_valores_w IS NOT NULL AND ds_valores_w::text <> '') then

	if (position(')(' in ds_valores_w) > 0) then
		ds_codigos_w	:= substr(ds_valores_w,1,position(')(' in ds_valores_w));
		ds_codigos_w	:= substr(ds_codigos_w,2,length(ds_codigos_w) - 2);
		
		ds_opcoes_w	:= substr(ds_valores_w,position(')(' in ds_valores_w)+2,length(ds_valores_w));
		ds_opcoes_w	:= substr(ds_opcoes_w,1,length(ds_opcoes_w) -1);
		
		codigos_w	:= obter_lista_string2(ds_codigos_w,',');
		opcoes_w	:= obter_lista_string2(ds_opcoes_w,',');
		
		for	i in codigos_w.first..codigos_w.last loop	
			begin
			
			if (codigos_w(i) = ie_valor_p) then
				return opcoes_w(i);
			end if;
			
			end;
		end loop;
		
	end if;

end if;
return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
