CREATE OR REPLACE FUNCTION obter_desc_razao_social_pj (cd_cgc_p pessoa_fisica_empregador.cd_cgc%type) RETURNS varchar AS $body$
DECLARE


ds_return_w		pessoa_juridica.ds_razao_social%type;


BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	
	select	max(pj.ds_razao_social)
	into STRICT	ds_return_w
	from 	pessoa_juridica  pj
	where 	pj.cd_cgc  = cd_cgc_p;
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
