CREATE OR REPLACE FUNCTION obter_desc_regra_efd (nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE




ds_regra_w		varchar(255);


BEGIN

if (nr_seq_regra_p > 0) then

begin
select	substr(a.ds_titulo, 1, 255)
into STRICT	ds_regra_w
from	fis_regra_efd a
where	a.nr_sequencia = nr_seq_regra_p;


exception
when others then
	null;
end;


end if;

return	ds_regra_w;

end;
