CREATE OR REPLACE FUNCTION obter_desc_regra_email (ds_seq_regra_email_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);
ds_titulo_w		varchar(255);


c01 CURSOR FOR
SELECT 		substr(ds_titulo,1,255)
from		regra_envio_email_agenda
where		obter_se_contido(nr_sequencia,ds_seq_regra_email_p) = 'S';


BEGIN
if (ds_seq_regra_email_p IS NOT NULL AND ds_seq_regra_email_p::text <> '') then
	open c01;
		loop
		fetch c01 into
		ds_titulo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := ds_titulo_w;
			else
				ds_retorno_w := ds_retorno_w || ', ' || ds_titulo_w;
			end if;

			end;
		end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
