SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_regra_vanco_nsv ( nr_seq_regra_nsv_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ajuste_dose_w	varchar(3);
pr_ajuste_dose_w	double precision;
ds_retorno_w		varchar(255);
qt_horas_proced_w	varchar(15);


BEGIN

if (nr_seq_regra_nsv_p IS NOT NULL AND nr_seq_regra_nsv_p::text <> '') then
	select	max(ie_ajuste_dose),
			max(pr_ajuste_dose),
			max(qt_horas_proced)
	into STRICT	ie_ajuste_dose_w,
			pr_ajuste_dose_w,
			qt_horas_proced_w
	from	rep_nivel_serico
	where	nr_sequencia = nr_seq_regra_nsv_p;

	ds_retorno_w	:= '';

	if (ie_ajuste_dose_w IS NOT NULL AND ie_ajuste_dose_w::text <> '') then
		if (ie_ajuste_dose_w = 'M') then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(308185) || ' '; -- Manter dosagem atual
		elsif (ie_ajuste_dose_w = 'N') then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(308186) || ' '; -- Não administrar dose atual
		else
			if (coalesce(pr_ajuste_dose_w::text, '') = '') then
				ds_retorno_w	:= wheb_mensagem_pck.get_texto(308187); -- (Cadastro incorreto - Revisar)
			end if;

			if (ie_ajuste_dose_w = 'A') then
				ds_retorno_w	:= wheb_mensagem_pck.get_texto(308189, 'PR_AJUSTE_DOSE_W=' || pr_ajuste_dose_w); -- Aumentar dose atual em #@PR_AJUSTE_DOSE_W#@%
			elsif (ie_ajuste_dose_w = 'R') then
				ds_retorno_w	:= wheb_mensagem_pck.get_texto(308191, 'PR_AJUSTE_DOSE_W=' || pr_ajuste_dose_w); -- Reduzir dose atual em #@PR_AJUSTE_DOSE_W#@%
			end if;
		end if;

		if (qt_horas_proced_w IS NOT NULL AND qt_horas_proced_w::text <> '') then
			ds_retorno_w	:= ds_retorno_w || wheb_mensagem_pck.get_texto(308195, 'QT_HORAS_PROCED_W=' || qt_horas_proced_w); -- -> Próximo NSV em #@QT_HORAS_PROCED_W#@ horas
		end if;
	end if;
end if;

return	coalesce(ds_retorno_w, nr_seq_regra_nsv_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
