CREATE OR REPLACE FUNCTION obter_desc_relatorio ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
ds_titulo_w	varchar(255);
cd_classif_w	varchar(255);
cd_relat_w	bigint;


BEGIN 
select	coalesce(a.ds_titulo,''), 
	coalesce(a.cd_classif_relat,''), 
	coalesce(a.cd_relatorio,0) 
into STRICT	ds_titulo_w, 
	cd_classif_w, 
	cd_relat_w 
from	relatorio a 
where	a.nr_sequencia = nr_sequencia_p;
 
ds_retorno_w := substr((cd_classif_w || ' - ' || cd_relat_w || ' (' || ds_titulo_w || ')'),1,255);
 
return	ds_retorno_w;
 
end;
$body$
