CREATE OR REPLACE FUNCTION obter_desc_resposta_mensagem (ie_valor_retorno bigint, ie_tipo_mensagem bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(250) := null;


BEGIN
	select case
		when ie_valor_retorno = 0 then
			obter_desc_expressao(CASE WHEN ie_tipo_mensagem=0 THEN  311702  ELSE 327113 END )
		when ie_valor_retorno = 1 then
			obter_desc_expressao(327114)
		when ie_valor_retorno = 2 then
			obter_desc_expressao(302745)
	end tipoMsg
		into STRICT ds_retorno_w
	;

	return	ds_retorno_w;

end;
$body$
