SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_resultados_prm ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
Área de origem	'A'
Tipo de problema	'T'
Origem Ocorrência	'O'
Fator		'F'
Tipo de evento	'E'
*/
ds_retorno_w	varchar(255);


BEGIN

If (ie_opcao_p = 'A') then

	select  substr(ds_area,1,255)
	into STRICT 	ds_retorno_w
	from   	prm_area_origem
	where  	ie_situacao = 'A'
	and    	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'T') then

	select  substr(ds_problema,1,255)
	into STRICT 	ds_retorno_w
	from	prm_tipo_problema
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'E') then

	select  substr(ds_tipo_evento,1,255)
	into STRICT 	ds_retorno_w
	from	qua_tipo_evento
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'F') then

	select  substr(ds_fator,1,255)
	into STRICT 	ds_retorno_w
	from	prm_fator_contribuinte
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'O') then

	select  substr(ds_origem,1,255)
	into STRICT 	ds_retorno_w
	from	prm_origem_ocorrencia
	where	nr_sequencia = nr_sequencia_p;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
