SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_resultado_comp (nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(255);
ds_item_w		varchar(80);
nr_seq_item_w		bigint;
ds_retorno_w		varchar(500);



BEGIN


select	ds_resultado,
	nr_seq_item
into STRICT	ds_resultado_w,
	nr_seq_item_w
from	pe_item_resultado
where	nr_sequencia	= nr_seq_item_p;

select	ds_item
into STRICT	ds_item_w
from	pe_item_examinar
where	nr_sequencia	= nr_seq_item_w;

ds_retorno_w	:= ds_item_w ||' '||ds_resultado_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
