SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_result_dor ( nr_seq_result_dor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

-- D	=   Descrição
-- I	=   Intensidade
-- T	=   Turno
ds_retorno_w	varchar(255);


BEGIN

If (ie_opcao_p = 'D') then
	select  substr(ds_escala,1,255)
	into STRICT 	ds_retorno_w
	from   	escala_dor_regra
	where  	ie_situacao = 'A'
	and    	nr_sequencia = nr_seq_result_dor_p;

elsif (ie_opcao_p = 'I') then
	select  substr(qt_intensidade,1,5)
	into STRICT 	ds_retorno_w
	from   	escala_dor_regra
	where  	ie_situacao = 'A'
	and    	nr_sequencia = nr_seq_result_dor_p;

elsif (ie_opcao_p = 'T') then
	select  substr(ds_turno,1,255)
	into STRICT 	ds_retorno_w
	from   	turno_Atendimento
	where   nr_sequencia = nr_seq_result_dor_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
