SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_result_escala_dor ( cd_escala_dor_p text, qt_escala_dor_p bigint, nr_seq_result_dor_p bigint default null) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
qt_reg_w		bigint := 0;


BEGIN

if (nr_seq_result_dor_p IS NOT NULL AND nr_seq_result_dor_p::text <> '') then

	select	max(ds_escala)
	into STRICT	ds_retorno_w
	from	escala_dor_regra
	where	nr_sequencia	= nr_seq_result_dor_p
	and		ie_situacao = 'A';

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		return ds_retorno_w;
	end if;
end if;

if (coalesce(ds_retorno_w::text, '') = '') then

		if (cd_escala_dor_p	= 'CV') then -- Categórica verbal
			if (qt_escala_dor_p = 0) then
				return obter_desc_expressao(487811);
			elsif (qt_escala_dor_p = 2.5) then
				return obter_desc_expressao(487812);
			elsif (qt_escala_dor_p = 5) then
				return obter_desc_expressao(331188);
			elsif (qt_escala_dor_p = 7.5) then
				return obter_desc_expressao(487813);
			elsif (qt_escala_dor_p = 10) then
				return obter_desc_expressao(487814);
			end if;

		elsif (cd_escala_dor_p	= 'CN') then -- Categórica numérica
			if (qt_escala_dor_p <= 0) then
				return 'Ausência de Dor';
			elsif (qt_escala_dor_p <= 3) then
				return 'Dor de fraca intensidade';
			elsif (qt_escala_dor_p <= 6) then
				return 'Dor de intensidade moderada';
			elsif (qt_escala_dor_p <= 9) then
				return 'Dor de forte intensidade';
			elsif (qt_escala_dor_p >= 10) then
				return 'Dor de intensidade insuportável';
			end if;


		elsif (cd_escala_dor_p	= 'PD') then -- PAINAD
			if (qt_escala_dor_p <= 0) then
				return 'Ausência de Dor';
			elsif (qt_escala_dor_p <= 3) then
				return 'Dor de fraca intensidade';
			elsif (qt_escala_dor_p <= 6) then
				return 'Dor de intensidade moderada';
			elsif (qt_escala_dor_p <= 9) then
				return 'Dor de forte intensidade';
			elsif (qt_escala_dor_p >= 10) then
				return 'Dor de intensidade insuportável';
			end if;

		elsif (cd_escala_dor_p	= 'CFT') then -- COMFORT-B
			if (qt_escala_dor_p <= 10) then
				return 'Super sedado';
			elsif (qt_escala_dor_p <= 22) then
				return 'Sedado';
			elsif (qt_escala_dor_p <= 30) then
				return 'Pouco sedado';
			end if;

		end if;
end if;


return null;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
