CREATE OR REPLACE FUNCTION obter_desc_resul_score_flex_2 ( qt_pontos_p bigint, nr_seq_escala_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000) := null;


BEGIN
if (qt_pontos_p IS NOT NULL AND qt_pontos_p::text <> '') then
	begin

	select 	ds_resultado
	into STRICT	ds_retorno_w
	from   	eif_escala_ii_result
	where   qt_pontos_p between qt_pontos_min and qt_pontos_max
	and	nr_seq_escala = nr_seq_escala_p;

	exception
			when others then
				ds_retorno_w	:= null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
