CREATE OR REPLACE FUNCTION obter_desc_score_flex_suep (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_score_flex_w bigint;
nr_seq_score_flex_ii_w bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

select max(nr_seq_score_flex),
	   max(nr_seq_score_flex_ii)
into STRICT   nr_seq_score_flex_w,
	   nr_seq_score_flex_ii_w
from informacao_suep
where nr_sequencia = nr_sequencia_p;

if (nr_seq_score_flex_w IS NOT NULL AND nr_seq_score_flex_w::text <> '') then

select substr(obter_desc_eif(nr_seq_score_flex_w),1,255)
into STRICT ds_retorno_w
;

elsif (nr_seq_score_flex_ii_w IS NOT NULL AND nr_seq_score_flex_ii_w::text <> '') then

select 	substr(obter_desc_score_flex_2(nr_seq_score_flex_ii_w),1,255)
into STRICT ds_retorno_w
;

end if;

end if;

return	ds_retorno_w;

end;
$body$
