CREATE OR REPLACE FUNCTION obter_desc_semana ( dt_referencia_p timestamp, ie_mascara_p text, ie_idioma_p text) RETURNS varchar AS $body$
DECLARE


ds_semana_w	varchar(15);
ds_ate_w	varchar(5);


BEGIN
if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') and (ie_mascara_p IS NOT NULL AND ie_mascara_p::text <> '') then
