		ds_ate_w := ' ' || Wheb_mensagem_pck.get_texto(309245) || ' '; --' Ã  ';
		end;
	end if;

	ds_semana_w :=	to_char(to_date(obter_inicio_fim_semana(dt_referencia_p,'I'),'dd/mm/yyyy hh24:mi:ss'),ie_mascara_p) || ds_ate_w ||
			to_char(to_date(obter_inicio_fim_semana(dt_referencia_p,'F'),'dd/mm/yyyy hh24:mi:ss'),ie_mascara_p);
	end;
end if;
