CREATE OR REPLACE FUNCTION obter_desc_seq_acao_exec_teste ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(40);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	substr(a.nr_seq_apres || '.' || b.nr_seq_apres || '.' || c.nr_seq_apres,1,40)
	into STRICT	ds_retorno_w
	from	teste_soft_exec_acao c,
		teste_caso_teste_item b,
		teste_caso_teste a
	where	a.nr_sequencia = b.nr_seq_caso_teste
	and	b.nr_sequencia = c.nr_seq_item
	and	c.nr_sequencia = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
