CREATE OR REPLACE FUNCTION obter_desc_seq_ecog ( nr_sequencia_p text, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(240);
nr_seq_apresent_w varchar(10);
/*
1 - descrição da ecog
2- numero de sequencia de apresentação da ecog
*/
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	max(ds_nivel),
			max(nr_seq_apresent)
	into STRICT	ds_retorno_w,
			nr_seq_apresent_w
	from	nivel_capac_funcional_ecog
	where	nr_sequencia = nr_sequencia_p;

	if (ie_tipo_p = 1) then
		return	ds_retorno_w;
	else
		return	nr_seq_apresent_w;
	end if;
else
	return ''	;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
