CREATE OR REPLACE FUNCTION obter_desc_setor_atend ( cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w			varchar(100);


BEGIN
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	select	substr(obter_desc_expressao(max(cd_exp_setor_atend),max(ds_setor_atendimento)),1,100)
	into STRICT	ds_descricao_w
	from	setor_atendimento
	where	cd_setor_atendimento	= cd_setor_atendimento_p;
end if;

return ds_descricao_w;

end;
