CREATE OR REPLACE FUNCTION obter_desc_se_mat_consignado ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consignado_w		varchar(01);
ie_consignado_ww		varchar(01);

/*ie_consignado_ww:
	N = NÃ£o consignado
	S = Consignado
	A = Ambos
*/
BEGIN

select	max(ie_consignado)
into STRICT	ie_consignado_w
from	material
where	cd_material	= cd_material_p;

if (ie_consignado_w = '0') then
	ie_consignado_ww	:= 'N';
elsif (ie_consignado_w = '1') then
	ie_consignado_ww	:= 'S';
elsif (ie_consignado_w = '2') then
	ie_consignado_ww	:= 'A';
end if;

return ie_consignado_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
