CREATE OR REPLACE FUNCTION obter_desc_skill_os ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_skills_w 	varchar(200);
ds_skill_w	varchar(50);

c01 CURSOR FOR
SELECT 	distinct b.ds_skill
from	man_ordem_serv_skill a,
	skill_desenvolvimento b,
	man_ordem_servico c
where 	a.nr_seq_skill = b.nr_sequencia
and	a.nr_seq_ordem_serv = c.nr_sequencia
and 	c.nr_sequencia = nr_sequencia_p;



BEGIN

open c01;
loop
fetch c01 into
	ds_skill_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (coalesce(ds_skills_w::text, '') = '') then
			ds_skills_w := ds_skill_w;
		else
			if (ds_skill_w IS NOT NULL AND ds_skill_w::text <> '') then
				ds_skills_w := ds_skills_w || ', ' || ds_skill_w;
			end if;
		end if;
	end;

end loop;
close c01;

return 	ds_skills_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
