CREATE OR REPLACE FUNCTION obter_desc_solic_vaga ( cd_pessoa_fisica_p text, nr_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE

 
nr_seq_reserva_w	bigint;
ds_retorno_w	  varchar(255);
ie_solicitacao_w  varchar(110);
ie_clinica_w		varchar(110);
ds_separar_w     varchar(3);

 

BEGIN 
ds_separar_w := '';
 
select	coalesce(max(nr_sequencia),0), 
	  MAX(substr(Obter_Valor_Dominio(1407,IE_SOLICITACAO),1,110)), 
		MAX(substr(Obter_Valor_Dominio(17,IE_CLINICA),1,110)) 
into STRICT	nr_seq_reserva_w, 
		ie_solicitacao_w, 
		ie_clinica_w 
from	gestao_vaga 
where	cd_pessoa_fisica		=	cd_pessoa_fisica_p 
and  nr_atendimento = nr_atendimento_p 
and	dt_solicitacao 		<= 	clock_timestamp() 
and nr_sequencia = (SELECT coalesce(max(nr_sequencia),0) from	gestao_vaga 
					where	cd_pessoa_fisica		=	cd_pessoa_fisica_p 
					and  nr_atendimento = nr_atendimento_p 
					and	dt_solicitacao 		<= 	clock_timestamp());
 
if (ie_solicitacao_w IS NOT NULL AND ie_solicitacao_w::text <> '') then 
	ds_retorno_w := ie_solicitacao_w;
	ds_separar_w := ' - ';
end if;
 
if (ie_clinica_w IS NOT NULL AND ie_clinica_w::text <> '') then 
	ds_retorno_w := ds_retorno_w || ds_separar_w || ie_clinica_w;
end if;
 
			 
if 	nr_seq_reserva_w = 0 then 
	ds_retorno_w := Wheb_mensagem_pck.get_texto(309196); --'NÃ£o'; 
end if;
						 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
