SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_solucao_adep ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_solucao_w	varchar(255);
ds_material_w	varchar(255);
ds_retorno_w	varchar(255);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	max(a.ds_material)
	into STRICT	ds_material_w
	from	material a,
		prescr_material b
	where	b.nr_prescricao		= nr_prescricao_p
	and	a.cd_material		= b.cd_material
	and	b.nr_sequencia_solucao	= nr_seq_solucao_p
	and	a.ie_ancora_solucao	= 'S';

	select	ds_solucao
	into STRICT	ds_solucao_w
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p;

	if (ds_solucao_w IS NOT NULL AND ds_solucao_w::text <> '') then
		ds_retorno_w	:= substr(ds_solucao_w,1,240);
	elsif (ds_material_w IS NOT NULL AND ds_material_w::text <> '') then
		ds_retorno_w	:= substr(ds_material_w,1,240);
	else
		ds_retorno_w	:= substr(obter_prim_comp_sol(nr_prescricao_p,nr_seq_solucao_p),1,240);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
