CREATE OR REPLACE FUNCTION obter_desc_status_acha_perd (nr_seq_status_achado_perdido_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_achado_perdido_w	varchar(80);

BEGIN

if (nr_seq_status_achado_perdido_p > 0) then

	select	substr(ds_status,1,80)
	into STRICT	ds_status_achado_perdido_w
	from	status_achado_perdido
	where	nr_sequencia = nr_seq_status_achado_perdido_p;
end if;

return	ds_status_achado_perdido_w;

end;
$body$
