CREATE OR REPLACE FUNCTION obter_desc_status_cheque (NR_SEQ_CHEQUE_P bigint) RETURNS varchar AS $body$
DECLARE

 
ie_status_cheque_w		smallint;
ds_status_cheque_w		varchar(255);


BEGIN 
 
SELECT	obter_status_cheque(nr_seq_cheque) 
INTO STRICT	ie_status_cheque_w 
FROM	CHEQUE_CR 
WHERE	NR_SEQ_CHEQUE	= NR_SEQ_CHEQUE_P;
 
if (ie_status_cheque_w = 1) then 
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301545); -- À Depositar 
elsif (ie_status_cheque_w = 2) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301546); -- Depositado 
elsif (ie_status_cheque_w = 3) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301547); -- Devolvido 
elsif (ie_status_cheque_w = 4) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301548); -- Reapresentado 
elsif (ie_status_cheque_w = 5) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301549); -- Segunda devolução 
elsif (ie_status_cheque_w = 6) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301550); -- Devolução paciente 
elsif (ie_status_cheque_w = 7) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301552); -- Vendido para terceiros 
elsif (ie_status_cheque_w = 8) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301560); -- Sacado 
elsif (ie_status_cheque_w = 9) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301561); -- Segunda reapresentação 
elsif (ie_status_cheque_w = 10) then
	ds_status_cheque_w	:= wheb_mensagem_pck.get_texto(301562); -- Terceira devolução 
end if;
 
return ds_status_cheque_w;
 
end;
$body$
