CREATE OR REPLACE FUNCTION obter_desc_status_cir_grid (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w      varchar(255);
dt_prevista_w 	  timestamp;
dt_inicio_real_w  timestamp;
dt_fim_w	  timestamp;
ie_status_w   varchar(10);


BEGIN
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select 		dt_inicio_prevista,
			dt_inicio_real,
			dt_termino,
			ie_status_cirurgia
	into STRICT		dt_prevista_w,
			dt_inicio_real_w,
			dt_fim_w,
			ie_status_w
	from		cirurgia
	where 		nr_cirurgia = nr_cirurgia_p;


	if (ie_status_w = 3) then
		ds_retorno_w := Wheb_mensagem_pck.get_texto(309198); --'Cancelada';
	end if;

	if (ie_status_w = 4) then
		ds_retorno_w := Wheb_mensagem_pck.get_texto(309202); --'Interrompida';
	end if;

	if (ie_status_w <> 3) and (ie_status_w <> 4) then
		if (dt_prevista_w IS NOT NULL AND dt_prevista_w::text <> '') and (coalesce(dt_inicio_real_w::text, '') = '') then
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309203); --'Prevista';
		elsif (dt_prevista_w IS NOT NULL AND dt_prevista_w::text <> '') and (dt_inicio_real_w IS NOT NULL AND dt_inicio_real_w::text <> '') and (coalesce(dt_fim_w::text, '') = '') then
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309204); --'Em andamento';
		elsif (dt_prevista_w IS NOT NULL AND dt_prevista_w::text <> '') and (dt_inicio_real_w IS NOT NULL AND dt_inicio_real_w::text <> '') and (dt_fim_w IS NOT NULL AND dt_fim_w::text <> '') then
			ds_retorno_w := Wheb_mensagem_pck.get_texto(309205); --'Conclu√≠da';
		end if;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
