SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_status_gv ( ie_status_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	varchar(50);
ds_retorno_w	varchar(255);

BEGIN

if (ie_status_p IS NOT NULL AND ie_status_p::text <> '') then

	
	select	cd,
		ds
	into STRICT	vl_retorno_w,
		ds_retorno_w
	from (
		SELECT	a.vl_dominio cd,
			substr(obter_valor_dominio(1408, a.vl_dominio),1,255) ds
		from    valor_dominio a
		where   a.cd_dominio = 1408
		and	a.vl_dominio = ie_status_p
		and	not exists (	SELECT	1
					from   	gestao_vaga_status b
					where  	b.ie_status = a.vl_dominio
					and	b.ie_situacao = 'A')
		
union

		select	b.ie_status cd,
			substr(max(b.ds_status),1,255)	ds
		from   	gestao_vaga_status b
		where  	b.ie_situacao = 'A'
		and	b.ie_status = ie_status_p
		group by ie_status
		order by 2
		) alias6;

end if;

if ((coalesce(ie_opcao_p::text, '') = '') or (ie_opcao_p = 'V')) then
	return	ie_status_p;
elsif (ie_opcao_p = 'D') then
	return	ds_retorno_w;
else
	return	vl_retorno_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_desc_status_gv ( ie_status_p text, ie_opcao_p text) FROM PUBLIC;

