CREATE OR REPLACE FUNCTION obter_desc_status_os (nr_sequencia_os_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_ordem_w		varchar(100);


BEGIN

if (nr_sequencia_os_p IS NOT NULL AND nr_sequencia_os_p::text <> '') then
	begin
	select	substr(obter_desc_expressao(CASE WHEN a.ie_status_ordem='1' THEN  283054 WHEN a.ie_status_ordem='2' THEN  296477 WHEN a.ie_status_ordem='3' THEN  310386 END ),1,255) ie_status_ordem
	into STRICT	ds_status_ordem_w
	from	man_ordem_servico a
	where 	a.nr_sequencia	= nr_sequencia_os_p;

	end;
end if;

return	ds_status_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
