CREATE OR REPLACE FUNCTION obter_desc_status_pato_dom ( nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_status_w		varchar(255);
ie_status_patologia_w	varchar(10);


BEGIN

select	coalesce(max(ie_status_patologia),'PL')
into STRICT	ie_status_patologia_w
from	proced_patologia_status
where	nr_sequencia	= nr_seq_status_p;

ds_status_w	:= ie_status_patologia_w;

if (ie_opcao_p = 'D') then
	select	max(obter_valor_dominio(3190,ie_status_patologia_w))
	into STRICT	ds_status_w
	;
end if;

return	ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
